mapper.insert:
/* LogMapper.insertLogArchiveVisit */
INSERT INTO LOG_ARCHIVE_VISIT (
    LOG_UID, USER_UID, VIEW_DATE, IP, DEVICE, BROWSER
)
SELECT
    #{logUid}
    , #{currentArchiveUserUid}
    , NOW()
    , #{ip}
    , #{device}
    , #{browser}
FROM 
	DUAL
WHERE NOT EXISTS (
    SELECT 
    	1
    FROM 
    	LOG_ARCHIVE_VISIT
    WHERE 
    	USER_UID 	= #{currentArchiveUserUid}
      	AND IP 		= #{ip}
      	AND VIEW_DATE > DATE_SUB(NOW(), INTERVAL 60 MINUTE)
)

/* LogMapper.insertLogArticleView */
INSERT INTO LOG_ARTICLE_VIEW (
    LOG_UID, ARTICLE_UID, VIEW_DATE, IP, DEVICE, BROWSER
)
SELECT
    #{logUid}
    , #{currentArticleUid}
    , NOW()
    , #{ip}
    , #{device}
    , #{browser}
FROM 
	DUAL
WHERE NOT EXISTS (
    SELECT 
    	1
    FROM 
    	LOG_ARTICLE_VIEW
    WHERE 
    	ARTICLE_UID = #{currentArticleUid}
      	AND IP 		= #{ip}
      	AND VIEW_DATE > DATE_SUB(NOW(), INTERVAL 5 MINUTE)
)
